package OOP.week3_day1;import OOP.week1.student.Student;import java.util.Arrays;/** * Created by Lisa on 11/4/2016. */public class FinalWord {    private final String name;    private final int age;    private final Student[] students;    public FinalWord(String name, int age, Student[] students) {        this.name = name;        this.age = age;        this.students = students;    }    @Override    public String toString() {        return "FinalWord{" +                "name='" + name + '\'' +                ", age=" + age +                ", students=" + Arrays.toString(students) +                '}';    }    public String getName() {        return name;    }    public int getAge() {        return age;    }    public Student[] getStudents() {        return students;    }}