package OOP.week5_day1.cloneDemo;/** * Created by Lisa on 11/5/2016. */public class Person implements Cloneable {    private String name;    private int age;    public Person(String name, int age) {        this.name = name;        this.age = age;    }    @Override    public String toString() {        return "Man{" +                "name='" + name + '\'' +                ", age=" + age +                '}';    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    @Override    public Person clone() throws CloneNotSupportedException {        return (Person) super.clone();    }}